import 'bootstrap/dist/css/bootstrap.css';
import './Menu.scss';
import HamburgerMenu from 'react-hamburger-menu';
import MenuContent from './MenuContent/MenuContent';
import ImgHeader from '../assets/img/conocenos/open.png';

export function Menu () {

  const [open, setOpen] = useState(false);
  const [isActive, setActive] = useState("false");

  function handleClickOpen(){
  //  setOpen(true)
  setOpen(prevOpen => !prevOpen);
  setActive(!isActive);
}


    return (
        <header>
          <nav className="container-fluid container-header px-0">   
         <div className="row w-100 container-title mx-0 position-relative">
         <div className="hidden col">
            <HamburgerMenu
    isOpen={open}
    menuClicked={handleClickOpen}
    width={18}
    height={15}
    strokeWidth={1}
    rotate={0}
    color='black'
    borderRadius={0}
    animationDuration={0.5}
/>
</div>   
              <div className="col-8  container-title">
                <h2 className="title">Casa A Pasada</h2> 
                <p className="title-num">tlfn: 981 483 623</p>
              </div>
            </div>
            {/* <div className={isActive ? "hiddenContent" : null}> <MenuContent/></div>   */}
            <div className={isActive ? "fadeOut" : "fadeIn"}> <MenuContent/></div>

            </nav> 
            <div className="img-header"></div>  
        </header>
    )
}

export default Menu;
